let nowTime = todatetime("@{variables('nowTime')}");
let lastRecievedTime = toscalar (
    union isfuzzy=true
        (
           WebSession_Summarized_DstIP_CL 
            | summarize max(EventTime_t)
            | extend max_TimeGenerated = datetime_add('hour',1,bin(max_EventTime_t,1h))
        ),
        (
            print (nowTime)
            | extend max_TimeGenerated = datetime_add('day',-1,bin(print_0,1h)) 
            | project-away print_0
        )
        | summarize max(max_TimeGenerated)
    );
_Im_WebSession(starttime=lastRecievedTime,endtime=bin(nowTime,1h))
| where TimeGenerated < bin(nowTime,1h)
