let LastIngestionTime = toscalar (
    union isfuzzy=true 
        (
        WebSession_Summarized_SrcInfo_CL
        | where EventTime_t >= {TimeRange:start}
        | summarize LastIngestionTime=max(TimeGenerated)
        | extend max_TimeGenerated = datetime_add('hour', 1, LastIngestionTime)
        ),
        (
        print({TimeRange:start})
| extend max_TimeGenerated = print_0
| project max_TimeGenerated
)
| summarize maxTimeGenerated = max(max_TimeGenerated) 
);
let WebData =    
union isfuzzy=true 
(
_Im_WebSession(starttime=todatetime(LastIngestionTime), endtime=now(), eventresult='Success')
| where isnotempty(HttpRequestMethod) and HttpRequestMethod != "NA"
| summarize EventCount=tolong(count()) by HttpRequestMethod, bin(TimeGenerated, {TimeRange:grain})
),
(
WebSession_Summarized_SrcInfo_CL
| where EventTime_t >= {TimeRange:start}
| project HttpRequestMethod=HttpRequestMethod_s, EventCount=EventCount_d, EventTime=EventTime_t, EventResult=EventResult_s
| where isnotempty(HttpRequestMethod) and HttpRequestMethod != "NA" and EventResult =~ 'Success'
| summarize EventCount=tolong(sum(EventCount)) by HttpRequestMethod, bin(TimeGenerated=EventTime, {TimeRange:grain})
)
| summarize EventCount = sum(EventCount) by HttpRequestMethod, bin(TimeGenerated, {TimeRange:grain});
WebData
| summarize EventCount = sum(EventCount) by HttpRequestMethod
| join kind=inner (WebData
| make-series Trend = sum(EventCount) on TimeGenerated from {TimeRange:start} to now() step {TimeRange:grain} by HttpRequestMethod
) on HttpRequestMethod
| project HttpRequestMethod, EventCount, Trend
| order by EventCount desc
| take 25

