let Data = (
_SentinelAudit()
| where SentinelResourceType =="Analytic Rule"
| extend SentinelResourceId = tostring(ExtendedProperties.ResourceId)
);
let Total = (
Data
| summarize TotalEvents = count() by SentinelResourceId, RuleName=SentinelResourceName, Type=SentinelResourceKind
);
let Activity = (
Data
| summarize Count=count() by SentinelResourceId, Description
| extend   bag = bag_pack(Description,Count) 
| project-away Description, Count
| summarize obj_bag = make_bag(bag) by SentinelResourceId
| evaluate bag_unpack(obj_bag)
);
Total | join kind=inner Activity on SentinelResourceId
| project-away SentinelResourceId1

let Data =(
_SentinelHealth()
| where SentinelResourceKind in ({RuleType})
| where Status in ({Status})
| where Reason in ({Reason})
);
let Total =(
Data
| summarize Total=dcount(SentinelResourceId) by Status);
let Scheduled=(
Data
| where SentinelResourceKind == "Scheduled"
| summarize Scheduled =dcount(SentinelResourceId) by Status);
let NRT=(
Data
| where SentinelResourceKind == "NRT"
| summarize NRT =dcount(SentinelResourceId) by Status);
union Total, Scheduled, NRT
| summarize Unique_Rule= sum(Total), Scheduled= sum(Scheduled), NRT=sum(NRT) by Status
| extend StatusText = strcat("'",Status,"'")

let Data =(
_SentinelAudit()
| where SentinelResourceType =="Analytic Rule"
| where SentinelResourceKind in ({AuditRuleType})
| extend Caller= tostring(ExtendedProperties.CallerName)
| where Caller in ({Caller})
);
let Total =(
Data 
| summarize Total=count() by Description);
let Scheduled=(
Data
| where SentinelResourceKind == "Scheduled"
| summarize Scheduled =count() by Description);
let NRT=(
Data
| where SentinelResourceKind == "NRT"
| summarize NRT =count() by Description);
let MLBehaviorAnalytics=(
Data
| where SentinelResourceKind == "MLBehaviorAnalytics"
| summarize MLBehaviorAnalytics =count() by Description);
let MicrosoftSecurityIncidentCreation=(
Data
| where SentinelResourceKind == "MicrosoftSecurityIncidentCreation"
| summarize MicrosoftSecurityIncidentCreation =count() by Description);
let ThreatIntelligence=(
Data
| where SentinelResourceKind == "ThreatIntelligence"
| summarize ThreatIntelligence =count() by Description);
let Fusion=(
Data
| where SentinelResourceKind == "Fusion"
| summarize Fusion =count() by Description);
union Total, Scheduled, NRT, MLBehaviorAnalytics, MicrosoftSecurityIncidentCreation, ThreatIntelligence, Fusion
| summarize TotalEvents= sum(Total), Scheduled= sum(Scheduled), NRT=sum(NRT), MLBehaviorAnalytics=sum(MLBehaviorAnalytics), MicrosoftSecurityIncidentCreation= sum(MicrosoftSecurityIncidentCreation), ThreatIntelligence=sum(ThreatIntelligence), Fusion=sum(Fusion) by Description
| extend DescriptionText = strcat("'",Description,"'")
