content.query:
let storageAccounts = 
union StorageBlobLogs
| extend ResourceId = trim_end("/blobservices/default", _ResourceId)
| summarize by ResourceId, Category
| extend categories = pack("Category", Category)
| summarize Categories=makelist(categories) by ResourceId=tolower(ResourceId);

let diagnosticLogs =
AzureDiagnostics
| extend ResourceType = strcat(ResourceProvider, "/", ResourceType)
| where ResourceType in~ ({parameterResourceType})
| summarize by ResourceId, Category
| extend categories = pack("Category", Category)
| summarize Categories=makelist(categories) by ResourceId=tolower(ResourceId);

let appServiceLogs =
union AppServiceAppLogs, AppServiceAuditLogs, AppServiceConsoleLogs, AppServiceIPSecAuditLogs, AppServicePlatformLogs, AppServiceHTTPLogs
| summarize by _ResourceId, Category
| extend categories = pack("Category", Category)
| summarize Categories=make_list_if(categories, Category != "") by ResourceId = tolower(_ResourceId);

let containerRegistryLogs =
union ContainerRegistryLoginEvents, ContainerRegistryRepositoryEvents
| summarize by _ResourceId, Category
| extend categories = pack("Category", Category)
| summarize Categories=make_list_if(categories, Category != "") by ResourceId = tolower(_ResourceId);

let bastionLogs =
MicrosoftAzureBastionAuditLogs
| extend Category = "MicrosoftAzureBastionAuditLogs"
| extend categories = pack("Category", Category)
| summarize Categories=make_list_if(categories, Category != "") by ResourceId = tolower(_ResourceId);

union diagnosticLogs, storageAccounts, appServiceLogs, containerRegistryLogs, bastionLogs

let ReferenceTables = datatable (TableName:string)[
"AuditLogs", "SigninLogs", "AADProvisioningLogs", "AADNonInteractiveUserSignInLogs", "AADServicePrincipalSignInLogs", "AADManagedIdentitySignInLogs"
"AADProvisioningLogs", "ADFSSignInLogs", "AADRiskyUsers", "AADUserRiskEvents", "AADRiskyServicePrincipals", "AADServicePrincipalRiskEvents"
];

let AuditLogs = AuditLogs | summarize by TenantId = tostring(split(ResourceId, "/")[2]), Source = "AuditLogs";
let SigninLogs = SigninLogs | summarize by TenantId = tostring(split(ResourceId, "/")[2]), Source = "SigninLogs";
let AADProvisioningLogs = AADProvisioningLogs | summarize by TenantId = AADTenantId, Source = "AADProvisioningLogs";
let AADTables = union withsource=SourceTable isfuzzy=true  AADNonInteractiveUserSignInLogs, AADServicePrincipalSignInLogs, AADManagedIdentitySignInLogs, ADFSSignInLogs, AADRiskyUsers, AADUserRiskEvents, AADRiskyServicePrincipals, AADServicePrincipalRiskEvents | summarize by Source = SourceTable, TenantId = "";

ReferenceTables
| join kind=leftouter (
union AuditLogs, SigninLogs, AADProvisioningLogs, AADTables
) on $left.TableName == $right.Source
| project TableName, LAWConnected=case(Source != "", "ðŸŸ¢Connected", "âšªDisconnected"), TenantId = case(TenantId != "", TenantId, "Undetermined (Tenant ID not logged for all tables.)")
| order by TenantId asc, LAWConnected desc
