content.query:
Cisco_Umbrella
| where TimeGenerated {TimeRange} 
|make-series Trend = count() on TimeGenerated from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain} by EventType;

Cisco_Umbrella
| where DvcAction contains "block"
| where TimeGenerated {TimeRange} 
|make-series Trend = count() on TimeGenerated from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain};

let CU_Total_Requests =
Cisco_Umbrella
| where TimeGenerated {TimeRange} 
| summarize count()
| extend evttype="Total Requests";

let CU_Total_Blocked =
Cisco_Umbrella
| where TimeGenerated {TimeRange} 
| where DvcAction contains "block"
| summarize count()
| extend evttype="Total Blocked";

let CU_Security_Blocked =
Cisco_Umbrella 
| where TimeGenerated {TimeRange} 
| where DvcAction contains "block"
| where isnotempty(ThreatCategory)
| summarize count()
| extend evttype="Security Blocked";

union CU_Security_Blocked,CU_Total_Blocked,CU_Total_Requests

let CU_proxy_outcoming_traffic =
Cisco_Umbrella
| where TimeGenerated {TimeRange} 
| where EventType == "proxylogs"
| extend TrafficType = "Outcoming", Bytes = SrcBytes
| project TrafficType, Bytes, TimeGenerated;

let CU_proxy_incoming_traffic =
Cisco_Umbrella
| where TimeGenerated {TimeRange} 
| where EventType == "proxylogs"
| extend TrafficType = "Incoming", Bytes = DstBytes
| project TrafficType, Bytes, TimeGenerated;


union CU_proxy_outcoming_traffic, CU_proxy_incoming_traffic
| make-series TotalGbytes = round(sum(Bytes/(1024*1024*1024)),2) on TimeGenerated  from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain} by TrafficType

Cisco_Umbrella
|where EventType == "cloudfirewalllogs"
| where DvcAction contains "BLOCK"
| where TimeGenerated {TimeRange} 
|make-series Trend = count() on TimeGenerated from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain};
