parameters.query:
let test1 = case('{IngestionTier}' == 'Minimal', dynamic('{MinimalEvents}'),
                '{IngestionTier}' == 'Common', dynamic('{CommonEvents}'),
                dynamic([]));
print test1

let manual_ids = () {
    print ids=todynamic("{parseManual}")
    | mvexpand ids
    | project ids = toint(ids)
};
let exclude_ids = () {
    print ids=todynamic("{parseExclude}")
    | mvexpand ids
    | project ids = toint(ids)
};
let tierIds = () {
    print ids = todynamic("{IngestArray}")
    | mvexpand ids
    | project ids = toint(ids)
};
let nsaIds = () {
    print  ids=todynamic("{nsaIds:escapejson}")
    | mvexpand ids
    | project ids = toint(ids.eventId)
};
union manual_ids, tierIds, nsaIds
| where isnotempty(ids)
| where ids !in (exclude_ids)
| summarize by ids
| sort by ids asc

let manual_ids = () {
    print ids=todynamic("{parseManualMitre}")
    | mvexpand ids
    | project ids = toint(ids)
};
let exclude_ids = () {
    print ids=todynamic("{parseExcludeMitre}")
    | mvexpand ids
    | project ids = toint(ids)
};
let tierIds = () {
    print ids = todynamic("{IngestArray}")
    | mvexpand ids
    | project ids = toint(ids)
};
let mitreIds = () {
    print  ids=todynamic('{mitreIds}')
    | mv-expand ids
    | project ids = toint(ids)
};
union manual_ids, tierIds, mitreIds
| where ids !in (exclude_ids)
| where isnotempty(ids)
| summarize by ids
| sort by ids asc

