print msft_ids
| mv-expand print_0
| project EventID = tostring(print_0.["Current Windows Event ID"]), Criticality = tostring(print_0.["Potential Criticality"]), Description = tostring(print_0.["Event Summary"])
| where Criticality in~ ({Criticality})
| distinct toint(EventID)
| where isnotempty(EventID)
| order by EventID asc

let manual_ids = () {
    print ids=todynamic("{parseManualMsft}")
    | mvexpand ids
    | project ids = toint(ids)
};
let exclude_ids = () {
    print ids=todynamic("{parseExcludeMsft}")
    | mvexpand ids
    | project ids = toint(ids)
};
let tierIds = () {
    print ids = todynamic("{IngestArray}")
    | mvexpand ids
    | project ids = toint(ids)
};
let msftIds = () {
    print  ids=todynamic('{msftIds}')
    | mv-expand ids
    | project ids = toint(ids)
};
union manual_ids, tierIds, msftIds
| where ids !in (exclude_ids)
| where isnotempty(ids)
| summarize by ids
| sort by ids asc

content.query:
// First Parse the NSA JSON
print events=todynamic({nsaRecommendedEvents:parsejson})
| extend events = events['Recommended Events to Collect']
// First we pivot the data
| mv-apply events on (
    extend Category = tostring(bag_keys(events)[0])
    | extend description = events[Category].description
    | extend events = events[Category].events
)
| mv-apply events on (
    extend eventName = tostring(bag_keys(events)[0])
    | extend eventId = tostring(bag_keys(events[eventName])[0])
    | extend properties = events[eventName][eventId]
    | project-away events
)
| evaluate bag_unpack(properties)
// group up all events
//| summarize events=makelist(eventId) by Category
//| extend events = replace_regex(tostring(events), @'"','')

