let manual_ids = () {
    print ids=todynamic("{parseManualUEBA}")
    | mvexpand ids
    | project ids = toint(ids)
};
let exclude_ids = () {
    print ids=todynamic("{parseExcludeUEBA}")
    | mvexpand ids
    | project ids = toint(ids)
};
let tierIds = () {
    print ids = todynamic("{IngestArray}")
    | mvexpand ids
    | project ids = toint(ids)
};
let uebaIds = () {
    print  ids=todynamic('{uebaIds}')
    | mv-expand ids
    | project ids = toint(ids)
};
union manual_ids, tierIds, uebaIds
| where ids !in (exclude_ids)
| where isnotempty(ids)
| summarize by ids
| sort by ids asc

