parameters.query:
//drop down parameter, list of subscriptions.
DSTIMAccess_CL
| where AggregationLastEventTime_t {TimeRange_IS} or TimeDiscovered_t {TimeRange_IS}
| project ResourceSubscriptionId = ResourceSubscriptionId_g, AggregationLastEventTime = AggregationLastEventTime_t, TimeGenerated = TimeDiscovered_t
| distinct ResourceSubscriptionId

//dropdown parameter, list of Resource groups according to Subscriptions_IS parameter
DSTIMAccess_CL | project ResourceSubscriptionId_g, ResourceGroup, AggregationLastEventTime_t, TimeDiscovered_t 
| where AggregationLastEventTime_t {TimeRange_IS} or TimeDiscovered_t {TimeRange_IS}
| where "{Subcriptions_IS}" == "'*'" or '{Subcriptions_IS:id}' has ResourceSubscriptionId_g
| distinct ResourceGroup

content.query:
//Query purpose = represent KPI cards with data about the access logs.
//Unique count of:Subscriptions, Resource Groups, Sensitive data sources accessed, Sensitive data assets accessed, Classifications accessed, Labels accessed and User Accounts.

let totalClassificationCount = GetClassificationList({TimeRange_IS:start}, {TimeRange_IS:end}, '{Subcriptions_IS:id}', dynamic([{ResourceGroup_IS}]) , dynamic([{UserAccount_IS}]))
| count;

let ExtraTiles = datatable(TableName: string, Count: string, tabName: string, expectedTab: string) 
[
 "Watchlist(s) match",  '{WatchListCount}', '{TabName}', 'UserAccount',
 "User Anomalies match",  '{AnomaliesUserEntitiesCount}', '{TabName}', 'UserAccount',
 "IP Anomalies match", '{AnomaliesIPEntitiesCount}','{TabName}', 'IPAddress',
 "TI match", '{TIIPCount}','{TabName}', 'IPAddress'
]
| where tabName == expectedTab | project TableName, Count;

let basicTiles = DSTIMCorrelatedLogs({TimeRange_IS:start}, {TimeRange_IS:end}, '{Subcriptions_IS:id}', dynamic([{ResourceGroup_IS}]) , dynamic([{UserAccount_IS}]))
| summarize dcount(ResourceSubscriptionId), dcount(ResourceGroup), dcount(StorageAccountName),dcount(Uri), dcount(SensitivityLabelName), dcount(RequesterUpn), dcount(RequesterAppId)
| project  ["Subscriptions"]  = dcount_ResourceSubscriptionId , ["Resource Groups"] = dcount_ResourceGroup , ["Sensitive data sources accessed"] = dcount_StorageAccountName, ["Sensitive data assets accessed"] = dcount_Uri, ["User accounts"] = iff("{UserAccount_IS}" != "'*'", array_length(dynamic([{UserAccount_IS}])),(dcount_RequesterUpn + dcount_RequesterAppId)) , ["Classifications accessed"] = toscalar(totalClassificationCount), ["Labels accessed"] = dcount_SensitivityLabelName
| evaluate narrow()
| extend TableName = replace(@"\[|\]|\'", @'',Column)
| extend Count = Value
| project TableName, Count
| union ExtraTiles;

basicTiles
