union withsource = _TableName *
| where TimeGenerated {TimeRange:query}
| summarize last_log = datetime_diff("second",now(), max(TimeGenerated))  by _TableName
| where last_log > {LastLogRecivedThreshold}
| project ['Table Name'] = _TableName,  ['Last Record Received'] =  last_log 
 | order by ['Last Record Received']  desc

