// lookup all Status states seen in last month 
let pastMonth = SecurityIncident | where TimeGenerated > startofday(ago(30d)) | summarize by Status;
 SecurityIncident
// lookup Status states, from today only
| where TimeGenerated > ago(1d)
| summarize todayCount=count() by Status
| join kind=fullouter (pastMonth) on Status
| join (
 SecurityIncident 
 // from oldest suplied parameter until 24hrs ago 
 | where TimeGenerated between (startofday({TimeRange:start}) .. ago(1d))
 // get avg from oldest date to this time yesterday
 | summarize count() by Status , bin(TimeGenerated,1d)
 | summarize historyCount = round(avg(count_),2) by Status
 ) on $left.Status1 == $right.Status
| extend todayCount = iif(isempty(todayCount),0,todayCount)
 | extend trendIs = case(
        todayCount > historyCount,"Trending Up",
        todayCount == historyCount,"Equal",
        todayCount < historyCount,"Trending Down",
        Status2
 )
| project Status2, todayCount, historyCount, trendIs

SigninLogs
| where TimeGenerated {TimeRange}
| where Status.errorCode {SigninStatus}
| where UserPrincipalName =~ "{UPN}"
| where Id == "{SigninId}"
| distinct tostring(DeviceDetail), UserAgent

