let SharePoint = (OfficeActivity | where OfficeWorkload has 'SharePoint' | summarize count() | extend isEnabled = iff(count_ > 0, 'True', 'False') | extend Table = 'SharePoint' | project Table, isEnabled);
let Exchange = (OfficeActivity | where OfficeWorkload has 'Exchange' | summarize count()| extend isEnabled = iff(count_ > 0, 'True', 'False') | extend Table = 'Exchange' | project Table, isEnabled);
let Teams = (OfficeActivity | where OfficeWorkload has 'MicrosoftTeams' | summarize count() | extend isEnabled = iff(count_ > 0, 'True', 'False')| extend Table = 'Teams' | project Table, isEnabled);
let OneDrive = (OfficeActivity | where OfficeWorkload has 'OneDrive' | summarize count() | extend isEnabled = iff(count_ > 0, 'True', 'False')| extend Table = 'OneDrive' | project Table, isEnabled);
let AA = (AzureActivity | summarize count() | extend isEnabled = iff(count_ > 0, 'True', 'False') | extend Table = 'AzureActivity' | project Table, isEnabled);
let DFC = (SecurityAlert | where ProviderName has 'Azure Security Center' | summarize count() | extend Table = 'Defender for Cloud Alerts' | extend isEnabled = iff(count_ > 0, 'True', 'False') | project Table, isEnabled);
let MCAS = (SecurityAlert | where ProviderName has 'MCAS' | summarize count() | extend Table = 'Defender for Cloud Apps Alerts' | extend isEnabled = iff(count_ > 0, 'True', 'False') | project Table, isEnabled);
let MDE = (SecurityAlert | where ProviderName has 'MDATP' | summarize count() | extend Table = 'Defender for Endpoint Alerts' | extend isEnabled = iff(count_ > 0, 'True', 'False') | project Table, isEnabled);
let MDO = (SecurityAlert | where ProviderName has 'OATP' | summarize count() | extend Table = 'Defender for Office Alerts' | extend isEnabled = iff(count_ > 0, 'True', 'False') | project Table, isEnabled);
let MDI = (SecurityAlert | where ProviderName has 'Azure Advanced Threat Protection' | summarize count() | extend Table = 'Defender for Identity Alerts' | extend isEnabled = iff(count_ > 0, 'True', 'False') | project Table, isEnabled);
SharePoint
| union Exchange, Teams, OneDrive, AA, DFC, MCAS, MDE, MDO, MDI
| order by Table asc

