let EUI = (EmailUrlInfo | summarize count() | extend Table = 'MDO Email URL Info' | extend isEnabled = iff(count_ > 0, 'True', 'False') | project Table, isEnabled);
let EAI = (EmailAttachmentInfo | summarize count() | extend Table = 'MDO Email Attachment Info' | extend isEnabled = iff(count_ > 0, 'True', 'False') | project Table, isEnabled);
