| union Audits, NonInt, SPS, SPR, PL, MI, ADFS, UR, RU, SE, WE, SL, CS, CP, PA, F5, FT, BC, EH, OI, ZS, FR, CA, IL, VN, CX, DVI, DNI, DPE, DNE, DFE, DRE, DLE, DILE, DE, DFCI, EV, EUI, ILE, IQE, IDE, CAE, TI
| order by Table asc

let SE = SecurityEvent | summarize count() by Computer | extend Table = 'SecurityEvent';
let SL = Syslog | summarize count() by Computer | extend Table = 'Syslog';
let CEF = CommonSecurityLog | summarize count() by Computer | extend Table = 'SecurityEvent';
SE
| union SL, CEF
| project Computer, CountofLogs = count_, Table
| order by CountofLogs desc

let BA = (BehaviorAnalytics | summarize count() | extend Table = 'BehaviorAnalytics' | extend isEnabled = iff(count_ > 0, 'True', 'False'));
let II = (IdentityInfo | summarize count() | extend Table = 'IdentityInfo' | extend isEnabled = iff(count_ > 0, 'True', 'False'));
let UAA = (UserAccessAnalytics | summarize count() | extend Table = 'UserAccessAnalytics' | extend isEnabled = iff(count_ > 0, 'True', 'False'));
let UPA = (UserPeerAnalytics | summarize count() | extend Table = 'UserPeerAnalytics' | extend isEnabled = iff(count_ > 0, 'True', 'False'));
BA 
| union II, UAA, UPA
| project Table, isEnabled
| sort by Table asc

