Usage 
| where IsBillable == true
| make-series BillableDataGB = sum(Quantity)/ 1000 default = 0 on TimeGenerated from {TimeRange:start} to {TimeRange:end} 
step {TimeRange:grain} 
| extend Title = "Ingestion Trend"
| extend Text = "{TimeRange:label}"

Usage 
| where IsBillable == true
| make-series BillableData = sum(Quantity)/ 1000 default = 0 on TimeGenerated from {TimeRange:start} to {TimeRange:end} 
step {TimeRange:grain}

union withsource = _TableName *
| where TimeGenerated {TimeRange:query}
| summarize last_log = datetime_diff("second",now(), max(TimeGenerated))  by _TableName
| where last_log > {LastDataReceived:seconds}
| project ['Table Name'] = _TableName,  ['Last Record Received'] =  last_log 
| join kind= leftouter (
Usage
| where StartTime {TimeRange:value}
| make-series Trend = sum(Quantity) default =0 on TimeGenerated from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain} by DataType
) on $left.['Table Name'] == $right.DataType
| project-away TimeGenerated, DataType
| order by ['Last Record Received']  desc

Usage
| take 1
| extend SearchJob = "{SearchJob}"
| extend SearchJob = substring(SearchJob, 1,strlen(SearchJob)-2)
| project SearchJob=split(SearchJob, ',')
| mv-expand SearchJob
| summarize SearchJob_Count = count() by bin(todatetime(SearchJob), 1d)
// | extend TimeGenerated = format_datetime(todatetime(SearchJob),'yyyy-MM-dd')
//| make-series SearchJob=count() default=0 on TimeGenerated in range(ago(730d), now(), 1d) 
//| order by SearchJob asc

let TCount = (SecurityIncident
| summarize arg_max(TimeGenerated, *) by IncidentName
| summarize TCount = count());
let WCount = (SecurityIncident
| where LastModifiedTime != CreatedTime
| where ModifiedBy has_any ('playbook', 'automation rule')
| summarize arg_max(TimeGenerated, *) by IncidentName
| summarize WCount = count());
let Percent = (WIncident:double, Total:double) {
    print WOCount = (Total-WIncident)
    | extend Percentage = (WOCount/Total) * 100
    | project Percentage
};
Percent(toscalar(WCount),toscalar(TCount))

find in (WindowsEvent, SecurityAlert, SecurityBaseline, SecurityBaselineSummary, SecurityDetection, SecurityEvent,WindowsFirewall, MaliciousIPCommunication, LinuxAuditLog, SysmonEvent, ProtectionStatus) where isnotempty(_BilledSize) project _BilledSize, Computer, TimeGenerated
// ASC Standard allows 500MB per machine - in a pool 
| summarize  allowedMBytes=500000000  * dcount(Computer), usedMBytes=sum(_BilledSize) , dcount(Computer)  by bin(TimeGenerated,{TimeRange:grain})

ThreatIntelligenceIndicator
// Select all indicators from the table
| where TimeGenerated < now()
// Select only indicators that have not expired
    and ExpirationDateTime > now()
// Select only indicators that are marked active
    and Active == true
// Select only the most recently ingested copy of an indicator
| summarize arg_max(TimeGenerated, *) by IndicatorId
// Summarize and order the data, then render the chart
| summarize CountOfIndicators = count() by SourceSystem
| order by CountOfIndicators desc 
| render barchart kind=unstacked

ThreatIntelligenceIndicator
// Select all indicators from the table
| where TimeGenerated < now()
// Select only indicators that have not expired
    and ExpirationDateTime > now()
// Select only indicators that are marked active
    and Active == true
// Select only the most recently ingested copy of an indicator
| summarize arg_max(TimeGenerated, *) by IndicatorId
// Create a new column to identify the type of indicator, IP, Domain, URL, File, or Other
| extend IndicatorType = iif(isnotempty(EmailSourceIpAddress) or isnotempty(NetworkDestinationIP) or isnotempty(NetworkIP) or isnotempty(NetworkSourceIP) or isnotempty(NetworkCidrBlock), "IP",
                        iff(isnotempty(Url), "URL",
                        iff(isnotempty(EmailRecipient) or isnotempty(EmailSenderAddress), "Email",
                        iff(isnotempty(FileHashValue), "File",
                         iff(isnotempty(DomainName) or isnotempty(EmailSourceDomain), "Domain",
                        "Other")))))
// Summarize and order the data, then render the chart
| summarize CountOfIndicators = count() by IndicatorType
| order by CountOfIndicators desc 
| render barchart kind=unstacked

ThreatIntelligenceIndicator
//| summarize arg_max(TimeGenerated, *) by IndicatorId
| summarize Count = count() by SourceSystem, bin(TimeGenerated, 1d)

ThreatIntelligenceIndicator
// Select all indicators from the table
| where TimeGenerated < now()
// Select only indicators that have not expired
    and ExpirationDateTime > now()
// Select only indicators that are marked active
    and Active == true
// Select only the most recently ingested copy of an indicator
| summarize arg_max(TimeGenerated, *) by IndicatorId
// Summarize and order the data, then render the chart
| summarize CountOfIndicators = count() by tostring(ConfidenceScore)
| order by CountOfIndicators desc 
| render piechart
