prancer_CL
| where data_data_resourceID_s != '' and data_data_resourceID_s contains '-'
| extend startPos = indexof(data_data_resourceID_s , "/subscriptions/") + strlen("/subscriptions/")
| extend endPos = indexof(data_data_resourceID_s , "/", startPos)
| extend subscriptionId = substring(data_data_resourceID_s , startPos, endPos - startPos)
| project subscriptionId
| summarize by subscriptionId

