let email_security_total_messages_timechart=ProofpointPOD
| where EventType == "message" 
| where TimeGenerated {TimeRange}
| extend event_type = "Message Rate";

let email_security_total_blocked_timechart =
ProofpointPOD
| where EventType == "message" 
| where FilterDisposition == "discard" or FilterDisposition == "reject"
| extend event_type = "Blocked Message Rate";

let quarantine_trends = 
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange}
| where isnotempty(FilterQuarantineFolder)
| extend event_type = "Quarantined Message Rate";

let result = union email_security_total_messages_timechart, email_security_total_blocked_timechart, quarantine_trends
| make-series Trend = dcount(MsgHeaderMessageId) on TimeGenerated from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain} by event_type;
result

let email_security_total_messages_processed =
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange} 
| summarize Count=dcount(MsgHeaderMessageId) 
| extend title="Total Messages Processed";

let email_security_inbound_messages_processed =
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange} 
| where NetworkDirection == "inbound"
| summarize Count=dcount(MsgHeaderMessageId) 
| extend title="Inbound Messages Processed";

let email_security_outbound_messages_processed =
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange} 
| where NetworkDirection == "outbound"
| summarize Count=dcount(MsgHeaderMessageId) 
| extend title="Outbound Messages Processed";

let email_security_total_blocked_messages =
ProofpointPOD
| where EventType == "message" 
| where TimeGenerated {TimeRange} 
| where FilterDisposition == "discard" or FilterDisposition == "reject"
| summarize Count=dcount(MsgHeaderMessageId) 
| extend title="Total Blocked Messages";

let email_security_total_quarantined_messages = 
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange}
| where isnotempty(FilterQuarantineFolder)
| summarize Count=dcount(MsgHeaderMessageId) 
| extend title = "Quarantined Messages";

let email_security_result_table = union email_security_total_messages_processed, email_security_inbound_messages_processed,email_security_outbound_messages_processed,email_security_total_blocked_messages,email_security_total_quarantined_messages; 
email_security_result_table 
| sort by Count

let inbound_tls_encrypted = 
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange}
| where FilterIsMsgEncrypted == "true" and NetworkDirection == "inbound"
| extend event_type = "Inbound TLS Encrypted";

let inbound_tls_unencrypted = 
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange} 
| where FilterIsMsgEncrypted == "false" and NetworkDirection == "inbound"
| extend event_type = "Inbound TLS Unencrypted";

let inbound_total = 
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange} 
| where NetworkDirection == "inbound"
| extend event_type = "Inbound Total";

let trend_result = union inbound_tls_encrypted, inbound_tls_unencrypted, inbound_total;
trend_result | summarize Count=dcount(MsgNormalizedHeaderMessageId) by event_type 
| join kind=inner (trend_result | make-series Trend = dcount(MsgNormalizedHeaderMessageId) default = 0 on TimeGenerated from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain}  by event_type) on event_type 
;

let inbound_tls_encrypted = 
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange}
| where FilterIsMsgEncrypted == "true" and NetworkDirection == "inbound"
| extend event_type = "Inbound TLS Encrypted";

let inbound_tls_unencrypted = 
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange} 
| where FilterIsMsgEncrypted == "false" and NetworkDirection == "inbound"
| extend event_type = "Inbound TLS Unencrypted";

let inbound_total = 
ProofpointPOD
| where EventType == "message"
| where TimeGenerated {TimeRange} 
| where NetworkDirection == "inbound"
| extend event_type = "Inbound Total";

let trend_result = union inbound_tls_encrypted, inbound_tls_unencrypted, inbound_total;
trend_result | summarize Count=dcount(MsgNormalizedHeaderMessageId) by event_type 
| join kind=inner (trend_result | make-series Trend = dcount(MsgNormalizedHeaderMessageId) default = 0 on TimeGenerated from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain}  by event_type) on event_type 
| order by Count desc
| project event_type, Trend, Count
;
