let maxdatapoints = 10000;
let starttime = {CustomTimeRange:start};
let endtime = {CustomTimeRange:end};
let day = datetime_diff('day',endtime,starttime);
let initialbinsize = case(day >=30, 1d, day >=7, 1d, day >=1,1h,5m);
let datapoints = (binsize : timespan){
union (audit() | where "Audit" in (selectedtables)), (application() | where "Application" in (selectedtables)),(process() | where "Process" in (selectedtables)),(user() | where "User" in (selectedtables)),(network() | where "Network" in (selectedtables)),(system() | where "System" in (selectedtables))
| where TimeGenerated >= {CustomTimeRange:start} and TimeGenerated <={CustomTimeRange:end} 
| where Severity in (severityParam)
| summarize Count=count() by Name, bin(TimeGenerated,binsize)};

let totalpoints = datapoints(initialbinsize) |summarize totalrows = count();
let inttotalpoints = toint(toscalar(totalpoints));
let binsizefactor = inttotalpoints/maxdatapoints +1;
let binsize = binsizefactor * initialbinsize;

