content.query:
{Table}
| getschema 
| project Column=tostring(ColumnName)
| sort by Column asc

//I want to update to exclude empty columns...

let CX = {Table} | count;
{Table}
| where {Column} == tostring(parse_json('{Selected}').{Column})
| project-away TenantId
| take 2000

let Events = dynamic([{Events}]) ;
let Devices = dynamic([{DeviceX}]) ;
let Accounts = dynamic([{AccountX}]) ;
let IPs = dynamic([{IPX}]) ;
let Files = dynamic([{FileX}]) ;
let EventTable = SecurityEvent | where EventID in (Events);
let DeviceTable = SecurityEvent | where Computer in (Devices);
let AccountTable = SecurityEvent | extend AccountKey = replace_string(Account, "\\", "") | where AccountKey in (Accounts);
let IPTable = SecurityEvent | where IpAddress in (IPs);
let FileTable = SecurityEvent | where tostring(array_reverse(split(FilePath, "\\")).[0]) in (Files);
union kind=inner isfuzzy=true
(EventTable),(DeviceTable), (AccountTable), (IPTable), (FileTable)
| extend AType = iif(AccountType=="User", "(U)", "(M)")
| extend Accounts = strcat(Account, " ", AType)
| project Time_and_Date=format_datetime(TimeGenerated, "MM/dd/yyyy HH:mm:ss"), tostring(EventID), _ResourceId, Devices=toupper(split(Computer, ".").[0]), Accounts, Category=case(Level==0, "Object Access", Level==2, "Event Processing", Level==4, "AppLocker", Level ==8, "Process Tracking", "Privilege Use"), Description=split(Activity, "-").[1], AccountDomain,AccountName,AccountType,AuthenticationPackageName,CallerProcessName,CommandLine,EventID,EventSourceName,FileHash,FilePath,IpAddress,IpPort,LogonGuid,LogonProcessName,LogonTypeName,MandatoryLabel,NewProcessId,NewProcessName,ObjectName,ObjectServer,ObjectType,ObjectValueName,OperationType,ParentProcessName,Process,ProcessName,RelativeTargetName,ServiceName,ShareName,SourceComputerId,SubjectAccount,SubjectDomainName,SubjectUserName,SubjectUserSid,TargetAccount,TargetDomainName,TargetInfo,TargetLogonGuid,TargetServerName,TargetSid,TargetUser,TargetUserName,TargetUserSid,Task,WorkstationName
| take 2000

let EventTable = Event | where EventLevelName contains "{Severity}"| where EventID in (dynamic([{Events}]));
let DeviceTable = Event | where EventLevelName contains "{Severity}"| where Computer in (dynamic([{DeviceX}]));
let AccountTable = Event | where EventLevelName contains "{Severity}"| extend New = split(UserName, "\\").[0] | where New in (dynamic([{AccountX}]));
let LogTable = Event | where EventLevelName contains "{Severity}"| extend Log2 = strcat(EventLog, "-", Source) | where Log2 in (dynamic([{LogX}]));
let DescriptionTable = Event | where EventLevelName contains "{Severity}"| extend Description = split(RenderedDescription, ".").[0] | where Description in (dynamic([{Error}]));
union kind=inner isfuzzy=true
(EventTable),(DeviceTable), (AccountTable), (LogTable), (DescriptionTable)
| parse RenderedDescription with *"SRUJet: " D1
| extend Description = iif(RenderedDescription has "SRUJet", D1, RenderedDescription)
| project L=case(EventLevelName=="Error", "🔴", EventLevelName=="Warning", "⚠️","ℹ️"), Level=EventLevelName, Time_and_Date=format_datetime(TimeGenerated, "MM/dd/yyyy HH:mm:ss"), _ResourceId, Computer, EventID, Source, UserName, Description
| sort by Time_and_Date
| take 2000

let Operation = AzureActivity | extend Operation2 = iif(OperationName == "", OperationNameValue, OperationName) | where Operation2 in (dynamic([{Operation}]));
let Status = AzureActivity | extend Status = case(ActivityStatusValue == "Succeeded", "Success", ActivityStatusValue == "Start", "Started", ActivityStatusValue == "Failure", "Failed", ActivityStatusValue == "Accept","Accepted", ActivityStatusValue) | where Status in (dynamic([{Status}]));
let CategoryValue = AzureActivity | where CategoryValue in (dynamic([{CategoryValue}]));
let Severity = AzureActivity | where Level in (dynamic([{Severity}]));
let Caller = AzureActivity | where Caller in (dynamic([{Caller}]));
let CallerIpAddress = AzureActivity | where CallerIpAddress in (dynamic([{CallerIp}]));
union kind=inner isfuzzy=true
(Operation), (Status), (CategoryValue), (Severity), (Caller), (CallerIpAddress)
| project TimeGenerated, OperationName, OperationNameValue, Level, ActivityStatusValue, ResourceGroup, Caller, CallerIpAddress, CategoryValue, HTTPRequest, Properties
| sort by TimeGenerated
| take 2000

let RiskLevel = {SourceTable} | where RiskLevel in (dynamic([{RiskLevel}]));
let RiskState = {SourceTable} | where RiskState in (dynamic([{RiskState}]));
let UserDisplayName = {SourceTable} | where UserDisplayName in (dynamic([{UserDisplayName}]));
union isfuzzy=true
(RiskLevel), (RiskState), (UserDisplayName)
| project TimeGenerated, UserDisplayName, UserPrincipalName, OperationName, Type, RiskLevel, RiskState, RiskDetail, IsProcessing, IsDeleted
| sort by TimeGenerated
| take 2000

let IpAddress = {SourceTable} | where IpAddress in (dynamic([{IpAddress}]));
let UserDisplayName = {SourceTable} | where UserDisplayName in (dynamic([{UserDisplayName}]));
let RiskLevel = {SourceTable} | where RiskLevel in (dynamic([{RiskLevel}]));
let RiskLocation = {SourceTable} | extend RiskLocation = strcat(Location.countryOrRegion, "-", Location.city) | where RiskLocation in (dynamic([{RiskLocation}]));
let RiskDetail = {SourceTable} | where RiskDetail in (dynamic([{RiskDetail}]));
let RiskEventType = {SourceTable} | where RiskEventType in (dynamic([{RiskEventType}]));
let RiskState = {SourceTable} | where RiskState in (dynamic([{RiskState}]));
union isfuzzy=true
(IpAddress), (UserDisplayName), (RiskLevel), (RiskLocation), (RiskDetail), (RiskEventType), (RiskState)
| sort by TimeGenerated
| take 2000

let Action = {SourceTable} | where Action in (dynamic([{Action}]));
let ResultType = {SourceTable} | where ResultType in (dynamic([{ResultType}]));
let App = {SourceTable} | extend TargetSystem = todynamic(TargetSystem)
| extend App = tostring(TargetSystem.Name) | where App in (dynamic([{App}]));
let ServicePrincipal = {SourceTable} | extend TargetSystem = todynamic(TargetSystem)
| extend ServicePrincipal= tostring(parse_json(tostring(TargetSystem.details)).ServicePrincipalDisplayName)| where ServicePrincipal in (dynamic([{ServicePrincipal}]));
union isfuzzy=true
(Action), (ResultType), (App), (ServicePrincipal)
| project TimeGenerated, OperationName, Action, ResultType, App, ServicePrincipal, ResultDescription, InitiatedBy, SourceIdentity, TargetIdentity
| sort by TimeGenerated
| take 2000

let ServicePrincipalName = {SourceTable} | where ServicePrincipalName in (dynamic([{ServicePrincipalName}]));
let ResourceDisplayName = {SourceTable} | where ResourceDisplayName in (dynamic([{ResourceDisplayName}]));
union isfuzzy=true
(ServicePrincipalName), (ResourceDisplayName)
| sort by TimeGenerated
| take 2000

let ResultDescription = {SourceTable} | where ResultDescription in (dynamic([{ResultDescription}]));
let Identity = {SourceTable} | where Identity in (dynamic([{Identity}]));
let AADOperationType = {SourceTable} | where AADOperationType in (dynamic([{AADOperationType}]));
let ActivityDisplayName = {SourceTable} | where ActivityDisplayName in (dynamic([{ActivityDisplayName}]));
let Category = {SourceTable} | where Category in (dynamic([{Category}]));
let ResultReason = {SourceTable} | where ResultReason in (dynamic([{ResultReason}]));
//let Crazy = {SourceTable} | where Crazy in (dynamic([{Crazy}]));
union isfuzzy=true
(ResultDescription), (Identity), (AADOperationType), (ActivityDisplayName), (Category), (ResultReason)//, (Crazy)
| project TimeGenerated, Identity, OperationName, ResultDescription, AADOperationType, ActivityDisplayName, Category, LoggedByService, Result, ResultReason
| sort by TimeGenerated
| take 2000

let Computer = CommonSecurityLog | where DeviceVendor in (dynamic([{DeviceVendor}])) | where Computer in (dynamic([{Computer}]));
let LogSeverity = CommonSecurityLog | where DeviceVendor in (dynamic([{DeviceVendor}])) | where LogSeverity in (dynamic([{LogSeverity}]));
let SourceIP = CommonSecurityLog | where DeviceVendor in (dynamic([{DeviceVendor}])) | where SourceIP in (dynamic([{SourceIP}]));
let DestinationIP = CommonSecurityLog | where DeviceVendor in (dynamic([{DeviceVendor}])) | where DestinationIP in (dynamic([{DestinationIP}]));
let Message = CommonSecurityLog | where DeviceVendor in (dynamic([{DeviceVendor}])) | where Message in (dynamic([{Message}]));
union kind=inner isfuzzy=true
(Computer), (LogSeverity), (SourceIP), (DestinationIP), (Message)
| project TimeGenerated, DeviceVendor, DeviceProduct, DeviceVersion, LogSeverity, Forwarder=Computer, DestinationIP, SourceIP, Activity, AdditionalExtensions, Message
| sort by TimeGenerated
| take 2000

let Severity = SecurityIncident | where Severity in (dynamic([{Severity}]));
let Title = SecurityIncident | where Title in (dynamic([{Title}]));
let Status = SecurityIncident | where Status in (dynamic([{Status}]));
let Assigned = SecurityIncident | extend Assigned = Owner.assignedTo | where Assigned in (dynamic([{Assigned}]));
let AlertCount = SecurityIncident | extend AlertCount = AdditionalData.alertsCount | where AlertCount in (dynamic([{AlertCount}]));
let ModifiedBy = SecurityIncident | where ModifiedBy in (dynamic([{ModifiedBy}]));
union isfuzzy=true
(Severity), (Title), (Status), (Assigned), (AlertCount), (ModifiedBy)
| extend Assigned = Owner.assignedTo
| extend AlertCount = AdditionalData.alertsCount
| project TimeGenerated, Severity, Title, Status, Assigned, AlertCount, ModifiedBy, IncidentUrl
| sort by TimeGenerated
| take 2000

let Status = SecurityAlert | where Status in (dynamic([{Status}]));
let AlertSeverity = SecurityAlert | where AlertSeverity in (dynamic([{AlertSeverity}]));
let AlertName = SecurityAlert | where AlertName in (dynamic([{AlertName}]));
let Entities = SecurityAlert | where Entities in (dynamic([{Entities}]));
let Tactics = SecurityAlert | where Tactics in (dynamic([{Tactics}]));
let Source = SecurityAlert | extend Source = iif(ProviderName startswith "ASI", replace_string(ProviderName, "ASI ", ""), ProductName)
| where Source in (dynamic([{Source}]));
union isfuzzy=true
(Status), (AlertSeverity), (AlertName), (Entities), (Tactics), (Source)
| extend Source = iif(ProviderName startswith "ASI", replace_string(ProviderName, "ASI ", ""), ProductName)
| project TimeGenerated, Status, AlertSeverity, AlertName, Description, Entities, Source, Tactics
| sort by TimeGenerated
| take 2000

let Computer = Syslog | where Computer in (dynamic([{Computer}]));
let Facility = Syslog | where Facility in (dynamic([{Facility}]));
let SeverityLevel = Syslog | where SeverityLevel in (dynamic([{SeverityLevel}]));
let SyslogMessage = Syslog | where SyslogMessage in (dynamic([{SyslogMessage}]));
union kind=inner isfuzzy=true
(Computer), (Facility), (SeverityLevel), (SyslogMessage)
| project TimeGenerated, Forwarder=Computer, Facility, SeverityLevel, SyslogMessage
| take 2000
