content.query:
Anomalies
| where TimeGenerated {TimeRange:query}
| count

//Currently there is no mapping with hosts
//We know that in the following anomalies there is a host present: "FirstTimeUserConnectedFromDevice", "DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "FirstTimeDeviceObservedInTenant", "DeviceUncommonlyUsedInTenant"
//We only look for "DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "DeviceUncommonlyUsedInTenant" though since the other two only state PII instead of the host itself
let Name = dynamic(["DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "DeviceUncommonlyUsedInTenant"]) ;
Anomalies
| where TimeGenerated {TimeRange:query}
| mv-expand AnomalyReasons
| where tostring(AnomalyReasons.Name) in (Name)
| count

//Currently there is no mapping for hosts
//We know that in the following anomalies there is a host present: "FirstTimeUserConnectedFromDevice", "DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "FirstTimeDeviceObservedInTenant", "DeviceUncommonlyUsedInTenant"
//We only look for "DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "DeviceUncommonlyUsedInTenant" though since the other two only state PII instead of the host itself
let Name = dynamic(["DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "DeviceUncommonlyUsedInTenant"]) ;
let TopHostsByAnomalies =
Anomalies
| mv-expand AnomalyReasons
| where tostring(AnomalyReasons.Name) in (Name)
| extend DeviceRegex = tostring(extract("with ([A-Z0-9-]+) device", 1, Description))
| extend DeviceName = tolower(DeviceRegex)
| summarize hint.strategy = shuffle AnomalyCount=count() by DeviceName;
let TopHostsIncidents = SecurityIncident
| summarize hint.strategy = shuffle arg_max(LastModifiedTime, *) by IncidentNumber
| mv-expand AlertIds
| project IncidentNumber, AlertId = tostring(AlertIds), TimeGenerated
| join kind= innerunique ( 
SecurityAlert
| mv-expand todynamic(Entities)
| where Entities["Type"] =~ "host"
| project SystemAlertId, DeviceName = tostring(tolower(Entities.HostName))
) on $left.AlertId == $right.SystemAlertId
| union TopHostsByAnomalies
| summarize IncidentCount=dcount(IncidentNumber), AlertCount=dcount(AlertId), AnomalyCount=sum(AnomalyCount) by DeviceName;
TopHostsIncidents
| where isnotempty(DeviceName)
| sort by IncidentCount, AlertCount, AnomalyCount, DeviceName desc

//Currently there is no mapping for hosts
//We know that in the following anomalies there is a host present: "FirstTimeUserConnectedFromDevice", "DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "FirstTimeDeviceObservedInTenant", "DeviceUncommonlyUsedInTenant"
//We only look for "DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "DeviceUncommonlyUsedInTenant" though since the other two only state PII instead of the host itself
let Name = dynamic(["DeviceUncommonlyUsedByUser", "DeviceUncommonlyUsedAmongPeers", "DeviceUncommonlyUsedInTenant"]) ;
Anomalies
| mv-expand AnomalyReasons
| where tostring(AnomalyReasons.Name) in (Name)
| extend DeviceName = tostring(extract("with ([A-Z0-9-]+) device", 1, Description))
| project TimeGenerated, Anomaly=Description, Score, RuleStatus, Tactics, Techniques, Entities, AnomalyDetails, AnomalyReasons, ExtendedProperties, ActivityInsights, DeviceInsights, UserInsights, ExtendedLinks
