let start = {TimeRange:start};
let end   = {TimeRange:end};
let MinCount = 5;
let PolicyProfileRaw = tostring("{PolicyProfile}");
let PolicyProfile = iif(PolicyProfileRaw in ("General","Stronger"), PolicyProfileRaw, "General");
// Computer universe
let AllComputers =
    SecurityEvent
    | where TimeGenerated between (start .. end)
    | summarize by Computer;
// Role heuristic: DCs emit 4768/4769/4771
let Roles =
    SecurityEvent
    | where TimeGenerated between (start .. end)
    | where EventID in (4768, 4769, 4771)
    | summarize IsDC = count() > 0 by Computer
    | extend Role = iff(IsDC, "Domain Controller", "Member/Workstation");
// Gate: any auth activity in period
let AuthActivity =
    SecurityEvent
    | where TimeGenerated between (start .. end)
    | where EventID in (4624,4625,4768,4769,4774,4775,4776,4777)
    | summarize AnyAuth = count() by Computer;
// Credential Validation outcome via Keywords 
let CredVal =
    SecurityEvent
    | where TimeGenerated between (start .. end)
    | where EventID in (4774, 4775, 4776, 4777)
    | extend Outcome =
        iff(Keywords == "0x8020000000000000", "Success",
        iff(Keywords == "0x8010000000000000", "Failure", "Unknown"))
    | summarize
        SuccessCnt = countif(Outcome == "Success"),
        FailureCnt = countif(Outcome == "Failure"),
        UnknownCnt = countif(Outcome == "Unknown"),
        Total = count()
      by Computer;
// Policy matrix
let Policy = datatable(Role:string, Profile:string, RequireSuccess:bool, RequireFailure:bool, SuccessOptional:bool)
[
  "Domain Controller",  "General",  false, true,  true,   // Success optional (IF), Failure required
  "Member/Workstation", "General",  true,  true,  false,
  "Domain Controller",  "Stronger", true,  true,  false,
  "Member/Workstation", "Stronger", true,  true,  false
];
// Order for final status
let StatusOrder = datatable(Status:string, Ord:int)
[
  "Missing (both)", 0,
  "Missing Failure", 1,
  "Missing Success", 2,
  "Optional Success missing", 3,
  "Partial", 4,
  "OK", 5,
  "N/A (no auth activity)", 6
];
let PerHost =
(
AllComputers
| join kind=leftouter Roles on Computer
| join kind=leftouter CredVal on Computer
| join kind=leftouter AuthActivity on Computer
| extend Role = coalesce(Role, "Member/Workstation"),
         AnyAuth    = tolong(coalesce(AnyAuth, 0)),
         SuccessCnt = tolong(coalesce(SuccessCnt, 0)),
         FailureCnt = tolong(coalesce(FailureCnt, 0))
| join kind=leftouter (Policy | where Profile == PolicyProfile) on Role
| extend SuccessOK = iff(RequireSuccess, SuccessCnt >= MinCount, true),
         FailureOK = iff(RequireFailure, FailureCnt >= MinCount, true)
| extend Status =
      iff(AnyAuth == 0, "N/A",
      iff(SuccessOK and FailureOK, "OK",
      iff(not(SuccessOK) and not(FailureOK), "Missing (both)",
      iff(not(SuccessOK), iff(SuccessOptional, "Optional Success missing", "Missing Success"),
      iff(not(FailureOK), "Missing Failure", "Partial")))))
| join kind=leftouter StatusOrder on Status
| project Computer, Role, Profile=PolicyProfile, MinRequired=MinCount, SuccessCnt, FailureCnt, Status
);
PerHost
| extend Bucket = iff(Status == "OK", "OK",iff(Status=="N/A", "N/A", "Not OK"))
| summarize Hosts = dcount(Computer) by Bucket
