(
  (
   // Pull Data Collection Rule Associations (DCRAs) from the provider-scoped table.
    // Each DCRA represents a binding between a Data Collection Rule (DCR) and a target resource (e.g., VM/Arc machine).
    (insightsresources
    | where type =~ 'microsoft.insights/datacollectionruleassociations'
    | extend pa=todynamic(properties))
    // Normalize the DCR id to lowercase for reliable joins later,
    // and derive the parent target resource id by stripping the DCRA suffix from the DCRA resource id (case-insensitive).
    | project dcrId=tolower(tostring(pa.dataCollectionRuleId)),
              targetResourceId=tostring(
                substring(tolower(id), 0,
                  indexof(tolower(id), '/providers/microsoft.insights/datacollectionruleassociations/')))
  )
  | join kind=inner (
      // From all DCRs, keep only those that:
      //  1) have dataFlows,
      //  2) include the Microsoft-SecurityEvent stream,
      //  3) send to the selected Log Analytics workspace (match by destination name).
      resources
      | where type =~ 'microsoft.insights/datacollectionrules'
      | extend p=todynamic(properties)
      | where isnotempty(p.dataFlows)
      | mv-expand flow=p.dataFlows
      | mv-expand stream=flow.streams
      | where stream =~ 'Microsoft-SecurityEvent'
      | mv-expand destName=flow.destinations
      | mv-expand la=p.destinations.logAnalytics
      | where tostring(la.name) == tostring(destName)
      // Normalize the workspace resource id and filter to the chosen workspace.
      | extend laWs=tolower(tostring(la.workspaceResourceId))
      | where laWs == tolower('{Workspace}')
      // Keep DCR identity and payload; normalize id to lowercase to match the DCRA join key.      
      | project dcrId=tolower(id), dcrName=name, p
      // From the DCR, enumerate windowsEventLogs data sources that send to Microsoft-SecurityEvent.      
      | mv-expand we=p.dataSources.windowsEventLogs
      | where array_index_of(we.streams, 'Microsoft-SecurityEvent') >= 0
      // Extract EventIDs from XPath queries (e.g., "...(EventID=4624) or (EventID=4625)...").
      | mv-expand xp=we.xPathQueries
      | extend ev = extract_all(@"EventID\s*=\s*(\d+)", tostring(xp))
      | mv-expand eventId = ev
      | extend eventId = toint(eventId)
      // Build a unique set of EventIDs per DCR (this is the collection fingerprint we will intersect across DCRs).
      | summarize eventIds = make_set(eventId, 5000) by dcrId, dcrName
    ) on dcrId
  // At this point we have, per target machine, each attached DCR and its explicit EventID set.    
  | project targetResourceId, dcrId, dcrName, eventIds
)
 // Build all unordered DCR pairs per target (machine) from the single list, without re-querying any remote table.
| summarize dcrs = make_list(pack('dcrId', dcrId, 'dcrName', dcrName, 'eventIds', eventIds))
  by targetResourceId
| mv-expand a = dcrs
| mv-expand b = dcrs
// Keep only one direction of each pair and drop self-pairs using a stable string comparison of DCR ids.
| extend a_dcr = tostring(a.dcrId), b_dcr = tostring(b.dcrId)
| extend cmp = strcmp(a_dcr, b_dcr)
| where cmp < 0 
// Compute the intersection of EventID sets for the DCR pair on the same machine.
| extend overlap = set_intersect(todynamic(a.eventIds), todynamic(b.eventIds))
// Only surface pairs that actually have overlapping EventIDs (i.e., potential duplicate collection).
| where array_length(overlap) > 0
// Final shape: machine, the two conflicting DCRs (as resource IDs for clickable links), and the list/count of overlapping EventIDs.
| project targetResourceId,
          dcrA = tostring(a.dcrId),
          dcrB = tostring(b.dcrId),
          overlapCount = array_length(overlap),
          overlapEventIDs = overlap
| order by overlapCount desc, targetResourceId asc

(
  (
    // Retrieve Data Collection Rule Associations (DCRAs) - each is a binding of a DCR to a target resource (e.g., VM/Arc server).
    // Use the provider-scoped table insightsresources for consistent discovery across subscriptions.
    (insightsresources
     | where type =~ 'microsoft.insights/datacollectionruleassociations'
     | extend pa=todynamic(properties))
    // Prepare join keys:
    //  - Normalize the DCR id to lowercase for exact string equality in joins.
    //  - Derive the parent (target) resource id by removing the association suffix from the DCRA id (case-insensitive).
    | project dcrId=tolower(tostring(pa.dataCollectionRuleId)),
              targetResourceId=tostring(
                substring(tolower(id), 0,
                  indexof(tolower(id), '/providers/microsoft.insights/datacollectionruleassociations/')))
  )
  | join kind=inner (
      // From all DCRs, keep only those that send the Microsoft-SecurityEvent stream to the selected workspace.
      resources
      | where type =~ 'microsoft.insights/datacollectionrules'
      | extend p=todynamic(properties)
       // Only consider DCRs that actually define dataFlows.
      | where isnotempty(p.dataFlows)
       // Unroll flows and streams to find Microsoft-SecurityEvent.
      | mv-expand flow=p.dataFlows
      | mv-expand stream=flow.streams
      | where stream =~ 'Microsoft-SecurityEvent'
      // Match each flow to its Log Analytics destination by name.
      | mv-expand destName=flow.destinations
      | mv-expand la=p.destinations.logAnalytics
      | where tostring(la.name) == tostring(destName)
      // Keep only flows targeting the chosen workspace (use lowercase for robust comparison).
      | extend laWs=tolower(tostring(la.workspaceResourceId))
      | where laWs == tolower('{Workspace}')
      // Preserve the DCR identity and payload for EventID extraction; normalize id to lowercase to match DCRA key.
      | project dcrId=tolower(id), dcrName=name, p
      // From each DCR, enumerate windowsEventLogs data sources that map to Microsoft-SecurityEvent.
      | mv-expand we=p.dataSources.windowsEventLogs
      | where array_index_of(we.streams, 'Microsoft-SecurityEvent') >= 0
      // Extract explicit EventIDs from XPath queries (e.g., "(EventID=4624) or (EventID=4625) ...").
      // Note: very broad XPaths (e.g., "Security!*") won't yield explicit IDs and thus won't appear here.
      | mv-expand xp=we.xPathQueries
      | extend ev = extract_all(@"EventID\s*=\s*(\d+)", tostring(xp))
      | mv-expand eventId = ev
      | extend eventId = toint(eventId)
      // Build a unique EventID set per DCR - this becomes the fingerprint for overlap checks.
      // (This does not subtract exclusions defined via transformKql)
      | summarize eventIds = make_set(eventId, 5000) by dcrId, dcrName
    ) on dcrId
  // At this point: for each target resource, we have each attached DCR and its set of explicitly collected EventIDs.
  | project targetResourceId, dcrId, dcrName, eventIds
)
// Consolidate all DCRs for a given target into a single list of objects so we can build pairs locally (no extra remote reads).
| summarize dcrs = make_list(pack('dcrId', dcrId, 'dcrName', dcrName, 'eventIds', eventIds))
  by targetResourceId
// Create all unordered DCR pairs per target by expanding the single list twice.
| mv-expand a = dcrs
| mv-expand b = dcrs
// Keep only one direction of the pair and exclude self-pairs via a stable string comparison on normalized DCR ids.
| extend a_dcr = tostring(a.dcrId), b_dcr = tostring(b.dcrId)
| extend cmp = strcmp(a_dcr, b_dcr)
| where cmp < 0
// Compute the intersection of EventID sets for the DCR pair on the same machine.
// If the intersection is non-empty, the pair is at risk of duplicate collection/billing for those EventIDs.
| extend overlap = set_intersect(todynamic(a.eventIds), todynamic(b.eventIds))
| where array_length(overlap) > 0
// Expand overlapping EventIDs to aggregate unique duplicate IDs and participating DCRs per target.
| mv-expand dupId = overlap
| summarize dupEventIDs = make_set(tolong(dupId), 5000),
            dcrIdsA = make_set(tostring(a.dcrId), 128),
            dcrIdsB = make_set(tostring(b.dcrId), 128)
  by targetResourceId
// Union left/right DCR ID sets to get the unique set of DCRs involved in any overlap on the target.
| extend dcrIds = set_union(dcrIdsA, dcrIdsB)
// Compute handy counters: number of overlapping DCRs and number of duplicate EventIDs.
| extend dcrCount = array_length(dcrIds),
         dupEventCount = array_length(dupEventIDs)
// Final shape for dashboarding: one row per target with counts, the duplicate EventID list, and the overlapping DCR IDs.
| project targetResourceId, dcrCount, dupEventCount, dupEventIDs, dcrIds
| order by dupEventCount desc, targetResourceId asc
