find in (WindowsEvent, SecurityAlert, SecurityBaseline, SecurityBaselineSummary, SecurityDetection, SecurityEvent,WindowsFirewall, MaliciousIPCommunication, LinuxAuditLog, SysmonEvent, ProtectionStatus) where isnotempty(_BilledSize) project _BilledSize, Computer, TimeGenerated
// ASC Standard allows 500MB per machine - in a pool 
| summarize  allowedMBytes=524288000  * dcount(Computer), usedMBytes=sum(_BilledSize) , dcount(Computer)  by bin(TimeGenerated,{TimeRange:grain})

//
// source: https://docs.microsoft.com/azure/security-center/security-center-enable-data-collection#data-collection-tier
//
let minimal = dynamic([1102,4624,4625,4657,4663,4688,4700,4702,4719,4720,4722,4723,4724,4727,4728,4732,4735,4737,4739,4740,4754,4755,
4756,4767,4799,4825,4946,4948,4956,5024,5033,8001,8002,8003,8004,8005,8006,8007,8222]);
SecurityEvent
| where EventID in (minimal)
| summarize Count = count(), BilledSize= sum(_BilledSize) by EventID, Activity
| order by EventID asc 
| join 
( 
    SecurityEvent
    | make-series Trend = count() on TimeGenerated from startofday({TimeRange:start}) to startofday({TimeRange:end}) step {TimeRange:grain} by EventID
    | extend (anomalies, score, baseline) = series_decompose_anomalies(Trend, 1.5, 7, 'linefit', 1, 'ctukey', 0.01)
    | extend Score = score[-1]
    | extend expectedEventCounts=baseline[-1], actualEventCount=Trend[-1], Score = score[-1], Trend
    | project-away score
) on EventID
| where Score > {AnomalyTuning} or Score < toreal(strcat("-",{AnomalyTuning}))
| project-away EventID1
| project Activity, expectedEventCounts, actualEventCount, Score, Trend, baseline, anomalies, Count, BilledSize
//https://docs.microsoft.com/azure/data-explorer/anomaly-detection#time-series-anomaly-detection

//
// source: https://docs.microsoft.com/azure/security-center/security-center-enable-data-collection#data-collection-tier
//
let common = dynamic([1,299,300,324,340,403,404,410,411,412,413,431,500,501,1100,1102,1107,1108,4608,4610,4611,4614,4622,
4624,4625,4634,4647,4648,4649,4657,4661,4662,4663,4665,4666,4667,4688,4670,4672,4673,4674,4675,4689,4697,
4700,4702,4704,4705,4716,4717,4718,4719,4720,4722,4723,4724,4725,4726,4727,4728,4729,4733,4732,4735,4737,
4738,4739,4740,4742,4744,4745,4746,4750,4751,4752,4754,4755,4756,4757,4760,4761,4762,4764,4767,4768,4771,
4774,4778,4779,4781,4793,4797,4798,4799,4800,4801,4802,4803,4825,4826,4870,4886,4887,4888,4893,4898,4902,
4904,4905,4907,4931,4932,4933,4946,4948,4956,4985,5024,5033,5059,5136,5137,5140,5145,5632,6144,6145,6272,
6273,6278,6416,6423,6424,8001,8002,8003,8004,8005,8006,8007,8222,26401,30004]);
SecurityEvent
| where EventID !in (common)
| summarize Count = count(), BilledSize=sum(_BilledSize) by EventID, Activity, TimeGenerated
| order by EventID asc 
| join 
( 
    SecurityEvent
    //
    // anomalies - seasonality baseline that captures the repetitive pattern. Outliers can be clearly spotted in the Score
    //
    | make-series Trend = count() on TimeGenerated from startofday({TimeRange:start}) to startofday({TimeRange:end}) step {TimeRange:grain} by EventID
    | extend (anomalies, score, baseline) = series_decompose_anomalies(Trend, 1.5, 7, 'linefit', 1, 'ctukey', 0.01)
    | extend Score = score[-1]
    | extend expectedEventCounts=baseline[-1], actualEventCount=Trend[-1], Score = score[-1], Trend
    | project-away score
) on EventID
| where Score > {AnomalyTuning} or Score < toreal(strcat("-",{AnomalyTuning}))
| project-away EventID1
| project Activity, expectedEventCounts, actualEventCount, Score, Trend, baseline, anomalies, Count, BilledSize

CommonSecurityLog
| where _IsBillable == true
// add price column 
| summarize sum(_BilledSize), estimatedPrice = sum(_BilledSize)/(1024*1024*1024) * {Price} by DeviceVendor, DeviceName, DeviceProduct
| order by DeviceVendor asc
// add cost by facility

Usage
| where StartTime {TimeRange:value}
| summarize sum(Quantity) by tostring(IsBillable)

//
// Predict data volume for the next month
//
let endDate   = now();     // what is the date now
let projectTo = now()+90d; // project forward nn days
let projectForward = 90;   // must be same as projectTo value
Usage
| where StartTime {TimeRange:value}
| where IsBillable == true
| make-series GBytesToday=sum(Quantity) default=0 on TimeGenerated from {TimeRange:start} to projectTo step 1d 
| extend GBytesForecast = series_decompose_forecast(GBytesToday, projectForward)

//union withsource = TableName1 *
//| where _IsBillable == True
//| summarize  totalGBytes =round(sum(_BilledSize/(1024*1024*1024)),2)  by bin(TimeGenerated, 7d)
Usage
| where StartTime {TimeRange:value}
| where IsBillable == True
| summarize  totalGBytes =sum(Quantity) by bin(TimeGenerated, 7d)
| order by TimeGenerated asc
| serialize 
| extend changeInGB = totalGBytes - prev(totalGBytes,1)
| extend pctChange = (changeInGB * 100) / prev(totalGBytes,1)
| extend TimeGenerated = strcat( format_datetime(TimeGenerated, 'yyyy-MM-dd'), ", Week of year: ", week_of_year(TimeGenerated) )

Usage
| where StartTime {TimeRange:value}
| summarize ['Table Size'] =sum(Quantity), Price = {Price} * sum(Quantity)/1000 by ['Table Name'] =DataType,['IsBillable'] =IsBillable
| order by ['Table Size']  desc
// | top 10 by ['Table Size']  desc

union withsource=TableName1 Event, SecurityEvent, Syslog, CommonSecurityLog
| where _IsBillable == true
| summarize ['Table Size'] = sum(_BilledSize), any(RenderedDescription), any(Activity) by TableName = TableName1 , EventID, Computer //, Activity, RenderedDescription, SyslogMessage
//| extend EventDescription = iif(isempty(Activity),RenderedDescription,Activity)
//| extend EventDescription = iif(isempty(EventDescription),SyslogMessage,EventDescription)
//| project-away RenderedDescription, SyslogMessage, Activity
| top 20 by ['Table Size']  desc
